INSERT INTO dram.dt_performances (id, title, author, type, duration, link, description, image, repertoire_id) VALUES (1, 'Сяй, мій божевільний діаманте', 'Айвон Менчелл', 'love—story з антрактом', '02:00:00', 'shine-my-crazy-diamond', 'Це вистава про трьох жінок поважного віку, вдовиць, котрі, не зважаючи ні на що, не втрачають почуття гумору, здорового глузду і особливо шарму.
INSERT INTO dram.dt_performances (id, title, author, type, duration, link, description, image, repertoire_id) VALUES (2, 'Срібний павук', 'За мотивами однойменного роману Василя Кожелянка', 'вестерн по-чернівецьки', '02:40:00', 'silver-spider', '"Срібний павук" – це перша вистава про таємниче, романтичне і трохи бандитське місто Чернівці за однойменним романом Василя Кожелянка. У виставі іронічно поєднано найдрібніші подробиці життя передвоєнних Чернівців 1939-го року і придумані історії, що служать лаштунками для пригодницької інтриги. Містифікація, іронія, побутові подробиці, романтичні і гумористичні діалоги – все це на історичному тлі. Вистава також прикрашена своєрідним гумором, соковитою буковинською говіркою та перепитіями любовного трикутника. За сюжетом, велику частину часу чернівецькі детективи Кароль та Гельмут проводять у ресторанах, ганяються за бандитами або... у душевних муках від нерозділеного кохання. Хоча саме ця безнадійна закоханість у красуню Марію дарує їм різні пригоди... У виставі зайнятий увесь акторський склад. Режисер-постановник – народна артистка України Людмила Скрипка. Учасники пластичних сцен – балет театру Юні члени організації Гітлер''югенд – вихованці театральної студії для дітей та молоді.', 'silver-spider.jpg', 1);